// src/App.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { Container, Row, Col, Form, Button, Card, Alert, Table } from "react-bootstrap";
import { Doughnut } from "react-chartjs-2";
import 'chart.js/auto';

const INITIAL = {
  BMI: "",
  Smoking: "No",
  AlcoholDrinking: "No",
  Stroke: "No",
  PhysicalHealth: 0,
  MentalHealth: 0,
  DiffWalking: "No",
  Sex: "Female",
  AgeCategory: "55-59",
  Race: "White",
  Diabetic: "No",
  PhysicalActivity: "Yes",
  GenHealth: "Very good",
  SleepTime: 7,
  Asthma: "No",
  KidneyDisease: "No",
  SkinCancer: "No",
};

function App() {
  const [form, setForm] = useState(INITIAL);
  const [risk, setRisk] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const saved = localStorage.getItem("pred_history");
    if (saved) setHistory(JSON.parse(saved));
  }, []);

  useEffect(() => {
    localStorage.setItem("pred_history", JSON.stringify(history));
  }, [history]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    // convert numeric fields properly
    const val = (name === "BMI" || name === "PhysicalHealth" || name === "MentalHealth" || name === "SleepTime")
      ? (value === "" ? "" : Number(value))
      : value;
    setForm((f) => ({ ...f, [name]: val }));
  };

  const validate = () => {
    if (form.BMI === "" || isNaN(form.BMI)) {
      setError("BMI must be a number");
      return false;
    }
    // add other validation rules if needed
    setError(null);
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;
    setLoading(true);
    setRisk(null);
    try {
      const res = await axios.post("http://localhost:8000/predict", form, {
        headers: { "Content-Type": "application/json" }
      });
      const prob = res.data.risk;
      setRisk(prob);
      const record = { timestamp: new Date().toISOString(), input: form, risk: prob };
      setHistory((h) => [record, ...h].slice(0, 50)); // keep last 50
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.detail || err.message || "Prediction failed");
    } finally {
      setLoading(false);
    }
  };

  const clearHistory = () => {
    setHistory([]);
    localStorage.removeItem("pred_history");
  };

  const donutData = risk != null ? {
    labels: ["Risk", "Remaining"],
    datasets: [{
      data: [Math.round(risk*100), 100 - Math.round(risk*100)],
      backgroundColor: [risk > 0.5 ? "#f44336" : (risk > 0.1 ? "#ff9800" : "#4caf50")],
      hoverOffset: 4
    }]
  } : null;

  return (
    <Container className="py-4">
      <h1 className="text-center mb-4">Heart Disease Risk Predictor</h1>

      <Row>
        <Col md={7}>
          <Card className="p-3 mb-3">
            <Form onSubmit={handleSubmit}>
              <Row>
                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>BMI</Form.Label>
                    <Form.Control name="BMI" type="number" step="0.1" value={form.BMI} onChange={handleChange} />
                  </Form.Group>
                </Col>
                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Age Category</Form.Label>
                    <Form.Select name="AgeCategory" value={form.AgeCategory} onChange={handleChange}>
                      <option>18-24</option><option>25-29</option><option>30-34</option><option>35-39</option>
                      <option>40-44</option><option>45-49</option><option>50-54</option><option>55-59</option>
                      <option>60-64</option><option>65-69</option><option>70-74</option><option>75-79</option>
                      <option>80 or older</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Smoking</Form.Label>
                    <Form.Select name="Smoking" value={form.Smoking} onChange={handleChange}>
                      <option>No</option><option>Yes</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Alcohol Drinking</Form.Label>
                    <Form.Select name="AlcoholDrinking" value={form.AlcoholDrinking} onChange={handleChange}>
                      <option>No</option><option>Yes</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Physical Activity</Form.Label>
                    <Form.Select name="PhysicalActivity" value={form.PhysicalActivity} onChange={handleChange}>
                      <option>Yes</option><option>No</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Gen Health</Form.Label>
                    <Form.Select name="GenHealth" value={form.GenHealth} onChange={handleChange}>
                      <option>Excellent</option><option>Very good</option><option>Good</option><option>Fair</option><option>Poor</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Sex</Form.Label>
                    <Form.Select name="Sex" value={form.Sex} onChange={handleChange}>
                      <option>Female</option><option>Male</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Race</Form.Label>
                    <Form.Select name="Race" value={form.Race} onChange={handleChange}>
                      <option>White</option><option>Black</option><option>Asian</option><option>Other</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Stroke</Form.Label>
                    <Form.Select name="Stroke" value={form.Stroke} onChange={handleChange}>
                      <option>No</option><option>Yes</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Diff Walking</Form.Label>
                    <Form.Select name="DiffWalking" value={form.DiffWalking} onChange={handleChange}>
                      <option>No</option><option>Yes</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Diabetic</Form.Label>
                    <Form.Select name="Diabetic" value={form.Diabetic} onChange={handleChange}>
                      <option>No</option><option>Yes</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Asthma</Form.Label>
                    <Form.Select name="Asthma" value={form.Asthma} onChange={handleChange}>
                      <option>No</option><option>Yes</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Kidney Disease</Form.Label>
                    <Form.Select name="KidneyDisease" value={form.KidneyDisease} onChange={handleChange}>
                      <option>No</option><option>Yes</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Skin Cancer</Form.Label>
                    <Form.Select name="SkinCancer" value={form.SkinCancer} onChange={handleChange}>
                      <option>No</option><option>Yes</option>
                    </Form.Select>
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Physical Health (days)</Form.Label>
                    <Form.Control name="PhysicalHealth" type="number" min="0" max="30" value={form.PhysicalHealth} onChange={handleChange} />
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Mental Health (days)</Form.Label>
                    <Form.Control name="MentalHealth" type="number" min="0" max="30" value={form.MentalHealth} onChange={handleChange} />
                  </Form.Group>
                </Col>

                <Col sm={6}>
                  <Form.Group className="mb-2">
                    <Form.Label>Sleep Time (hours)</Form.Label>
                    <Form.Control name="SleepTime" type="number" min="0" max="24" value={form.SleepTime} onChange={handleChange} />
                  </Form.Group>
                </Col>

                <Col sm={12} className="mt-2">
                  <Button type="submit" disabled={loading} className="w-100">
                    {loading ? "Calculating..." : "Calculate Risk"}
                  </Button>
                </Col>
              </Row>
            </Form>
          </Card>

          {error && <Alert variant="danger">{error}</Alert>}

          <Card className="p-3 mt-3">
            <Row>
              <Col md={6}>
                <h5>History</h5>
                <Button variant="outline-danger" size="sm" onClick={clearHistory}>Clear</Button>
                <Table size="sm" className="mt-2">
                  <thead><tr><th>Date</th><th>Risk%</th></tr></thead>
                  <tbody>
                    {history.map((h,i) => (
                      <tr key={i}><td>{new Date(h.timestamp).toLocaleString()}</td><td>{(h.risk*100).toFixed(2)}</td></tr>
                    ))}
                  </tbody>
                </Table>
              </Col>
              <Col md={6} className="text-center">
                <h5>Result</h5>
                {risk == null ? <p>No prediction yet</p> : (
                  <>
                    <Doughnut data={donutData} />
                    <h3 className="mt-2">{(risk*100).toFixed(2)}%</h3>
                    <p className="text-muted">Risk estimate (calibrated)</p>
                  </>
                )}
              </Col>
            </Row>
          </Card>
        </Col>

        <Col md={5}>
          <Card className="p-3">
            <h5>About</h5>
            <p>This demo app sends patient data to a REST API that returns a calibrated probability for heart disease. Use for demo/education only.</p>
            <hr />
            <p><strong>Note:</strong> The server must be running and accessible at <code>http://localhost:8000/predict</code>.</p>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
